from multiprocessing import Process
import os
import math

from reverse import reverse

CPUS = os.cpu_count()

def get_i_ranges():
    ranges = [[0,0]] * CPUS

    RANGE_SLICE = 256 // CPUS

    leftover = 256 - RANGE_SLICE*CPUS

    ranges = [[i*RANGE_SLICE, (i+1)*RANGE_SLICE] for i in range(CPUS)]
    
    ranges[-1][1] += leftover

    return ranges

