encoded = "e30f49ae6649ae6648a40c2c2c6c086e8649a5aceaa52eac"
MIN_SPACES = 4

def calc(i_start, i_end):
    for i in range(i_start, i_end):
        for j in range(0, 256):
            if j % 32 == 0:
                print('i,j:', i, j)
            for k in range(0, 256):
                for l in range(0, 256):
                    key = [i, j, k, l]

                    reverse(encoded, key, min_number_of_spaces_expected=MIN_SPACES)

if __name__ == '__main__':
    processes = []

    ranges = get_i_ranges()
    print('i ranges per core:', ranges)

    for i in range(CPUS):
        print('registering process', i)
        processes.append(Process(target=calc, args=[ranges[i][0], ranges[i][1]]))

    for p in processes:
        p.start()

    for p in processes:
        p.join()